mapper.update:
UPDATE CHAT_EMP
SET LAST_CNFIRM_NO = #{mssageSn}
WHERE CHTT_ROOM_NO = #{chttRoomNo}
AND EMPL_NO = #{emplNo}

UPDATE CHAT_ROOM
SET LAST_MSSAGE_NO = #{mssageSn}
WHERE CHTT_ROOM_NO = #{chttRoomNo}

mapper.select:
SELECT CR.CHTT_ROOM_NO, CHTT_ROOM_TY, CHTT_ROOM_NM, READ_COUNT, LAST_MSSAGE_NO, NVL(M.MSSAGE_CREAT_DT, CR.CHTT_CREAT_DT) AS CHTT_CREAT_DT, M.MSSAGE_CN as LAST_MSG, CE.EMPL_NO, M.MSSAGE_TY, E.EMPL_NM, E.PROFL_PHOTO_URL, E.EMPL_NO as TARGET_EMPL_NO FROM (SELECT B.CHTT_ROOM_NO, B.LAST_MSSAGE_NO, B.CHTT_CREAT_DT, B.CHTT_ROOM_NM, B.CHTT_ROOM_TY, NVL(B.LAST_MSSAGE_NO - A.LAST_CNFIRM_NO, 0) as READ_COUNT
FROM CHAT_EMP A LEFT JOIN CHAT_ROOM B ON (A.CHTT_ROOM_NO = B.CHTT_ROOM_NO) WHERE EMPL_NO = #{emplNo} /* 채팅방이 존재하는지 확인? */
AND B.CHTT_ROOM_NO = #{chttRoomNo}
AND NVL(B.LAST_MSSAGE_NO - A.LAST_CNFIRM_NO, 0) > 0 /* 이거 쓰임 ?*/
) CR LEFT JOIN CHAT_EMP CE ON(CE.CHTT_ROOM_NO = CR.CHTT_ROOM_NO AND CE.EMPL_NO != #{emplNo}) LEFT JOIN EMPLOYEE E ON (E.EMPL_NO = CE.EMPL_NO) LEFT JOIN CHAT_MESSAGE M ON (M.MSSAGE_SN = CR.LAST_MSSAGE_NO AND M.CHTT_ROOM_NO = CR.CHTT_ROOM_NO) ORDER BY CHTT_CREAT_DT DESC

SELECT EMPL_NO
FROM CHAT_EMP
WHERE CHTT_ROOM_NO = #{chttRoomNo}
AND EMPL_NO != #{mssageWritngEmpno}
