mapper.select:
SELECT E.EMPL_NM ,M.email_no ,M.empl_no ,M.lbl_no ,M.email_trnsmis_ty ,M.email_cl_ty ,M.trnsmit_email ,M.recptn_email ,M.email_sj ,M.email_cn ,M.atch_file_no ,M.trnsmit_dt ,M.readng_at ,M.email_group_no ,M.starred ,ML.LBL_NO ,ML.LBL_COL ,ML.LBL_NM FROM MAIL M INNER JOIN EMPLOYEE E ON M.EMPL_NO = E.EMPL_NO LEFT JOIN MAIL_LABEL ML ON M.lbl_no = ML.lbl_no WHERE M.EMPL_NO=#{searchVo.emplNo}
AND m.STARRED = 'Y'
AND m.EMAIL_CL_TY = #{searchVo.emailClTy}
AND M.DEL_AT = 'N'
AND m.LBL_NO = #{searchVo.lblNo}
AND m.EMAIL_SJ LIKE '%' || #{searchVo.keyword} || '%'
AND m.EMAIL_CN LIKE '%' || #{searchVo.keyword} || '%'
AND (m.EMAIL_SJ '%' || #{mailVO.keyword} || '%' OR m.EMAIL_CN LIKE '%' || #{searchVo.keyword} || '%')
ORDER BY TRNSMIT_DT DESC OFFSET (#{currentPage} - 1) * 10 ROWS FETCH NEXT 10 ROWS ONLY

mapper.update:
update MAIL
set readng_at = 'Y'
where email_no = #{emailNo}

mapper.select:
SELECT count(*) FROM mail m WHERE m.EMPL_NO = #{emplNo}
and m.STARRED = 'Y'
AND m.EMAIL_CL_TY = #{mailVO.emailClTy}
AND m.DEL_AT = 'N'
AND m.LBL_NO = #{mailVO.lblNo}
AND m.EMAIL_SJ LIKE '%' || #{mailVO.keyword} || '%'
AND m.EMAIL_CN LIKE '%' || #{mailVO.keyword} || '%'
AND (m.EMAIL_SJ LIKE '%' || #{mailVO.keyword} || '%' OR m.EMAIL_CN LIKE '%' || #{mailVO.keyword} || '%')

mapper.update:
BEGIN

