mapper.insert:
INSERT ALL /* 알림 추가 */

    INTO NOTIFICATION(
        NTCN_SN
        , EMPL_NO
        , NTCN_SJ
        , NTCN_CN
        , ORIGIN_PATH
        , SKILL_CODE
        , NTCN_CREAT_DT
    )
    VALUES (
        SEQ_NOTIFICATION.NEXTVAL
        , #{emplVO.emplNo}
        , #{ntcnSj}
        , #{ntcnCn}
        , #{originPath}
        , #{skillCode}
        ,SYSDATE
    )

SELECT 1 FROM DUAL

mapper.delete:
DELETE
FROM NOTIFICATION
WHERE EMPL_NO = #{emplNo}
  AND NTCN_SN = #{ntcnSn}

mapper.update:
UPDATE NOTIFICATION
SET NTCN_READ_YN = 'Y'
WHERE EMPL_NO = #{emplNo}

    AND NTCN_SN = #{ntcnSn}
