mapper.select:
SELECT SCHDUL_NO , EMPL_NO , LBL_NO , SCHDUL_TY , SCHDUL_SJ , SCHDUL_CN , SCHDUL_BEGIN_DT , SCHDUL_END_DT , SCHDUL_PLACE , DEPT_CODE FROM SCHEDULE WHERE ( (SCHDUL_TY = '0' AND EMPL_NO = #{emplNo}) OR (SCHDUL_TY = '1' AND DEPT_CODE = #{deptCode}) OR (SCHDUL_TY = '2') ) AND(
( schdul_ty IN
#{ty}
OR lbl_no IN
#{no}
OR (lbl_no IS NULL and SCHDUL_TY = '0')
)
schdul_ty IN
#{ty}
( lbl_no IN
#{no}
OR (lbl_no IS NULL and SCHDUL_TY = '0')
)
1=2
)

mapper.insert:
	/*kr.or.ddit.sevenfs.mapper.schedule.ScheduleMapper.scheduleInsert 이걸 찍으면 log에서 찾기 편하다.*/

		SELECT SEQ_SCHEDULE.NEXTVAL FROM DUAL  

INSERT INTO SCHEDULE (
					    SCHDUL_NO
					  , EMPL_NO

						 , LBL_NO

					   , SCHDUL_TY
					   , SCHDUL_SJ
					   , SCHDUL_CN
					   , SCHDUL_BEGIN_DT
					   , SCHDUL_END_DT
					   , DEPT_CODE
					  )
			VALUES(
				  #{schdulNo}
				, #{emplNo}

					, #{lblNo}

				, #{schdulTy}
				, #{schdulSj}
				, #{schdulCn}
				, #{schdulBeginDt}
				, #{schdulEndDt}
				, #{deptCode}
			)

mapper.update:
UPDATE schedule
SET

    	lbl_no = #{lblNo},


    	lbl_no = null,

      schdul_ty = #{schdulTy}
    , schdul_sj = #{schdulSj}
    , schdul_cn = #{schdulCn}
    , schdul_begin_dt = #{schdulBeginDt}
    , schdul_end_dt = #{schdulEndDt}
    , schdul_place = #{schdulPlace}
WHERE
    schdul_no = #{schdulNo}

UPDATE schedule
SET lbl_no = null
WHERE lbl_no = #{lblNo}
